import React, { Component } from 'react'
import { connect } from 'react-redux'
import { setAuthedUser } from '../actions/authedUser'
import { Redirect } from 'react-router-dom'


class Auth extends Component {

    state = {
        selectedUser: null,
        toDashboard: false,
    }

    authenticateUser = (e) => {
        const selectedUser = e.target.value
        this.setState(() => ({
            selectedUser
        }))
    }

    handleSubmit = (e) => {
        e.preventDefault()
        const { selectedUser } = this.state
        this.props.dispatch(setAuthedUser(selectedUser))

        this.setState(() => ({
            selectedUser: null, 
            toDashboard: true,
        }))
    }

    render() {
        const { users } = this.props
        const { selectedUser, toDashboard } = this.state
        if(toDashboard === true) {
            return <Redirect to='/' />
        }
        return(
            <div>
                <h3>Would You Rather...?</h3>
                <form onSubmit={this.handleSubmit}>
                    <select onChange={this.authenticateUser}>
                        <option key="default" value="default" disabled>Select User...</option>
                        {Object.keys(users).map((user, index) => (
                            <option key={index} value={users[user].id}>{users[user].name}</option>
                        ))}
                    </select>
                    <button type='submit' disabled={selectedUser === null || selectedUser === 'default'}>Sign In</button>
                </form>
            </div>
        )
    }
}

function mapStateToProps({ users })  {
    return {
        users
    }
}

export default connect(mapStateToProps)(Auth)
